import React, { useRef, useState } from 'react'
import './Share.css'
import { PermMedia, Label, Room, EmojiEmotions } from '@material-ui/icons'
import { useContext } from 'react'
import { AuthContext } from '../../context/AuthContext'
import axios from 'axios'
import { storage, ref } from '../../firebase/firebase'
export default function Share() {
	const allInputs = { imgUrl: '' }
	const [imageAsFile, setImageAsFile] = useState('')
	const [imageAsUrl, setImageAsUrl] = useState(allInputs)
	const { user } = useContext(AuthContext)
	const desc = useRef()
	const [file, setFile] = useState(null)
	const [isImage, setIsImage] = useState(false)

	console.log(imageAsFile)
	const handleImageAsFile = (e) => {
		const image = e.target.files[0]
		setImageAsFile((imageFile) => image)
	}

	const handleFireBaseUpload = (e) => {
		e.preventDefault()
		console.log('start of upload')
		// async magic goes here...
		if (imageAsFile === '') {
			console.error(`not an image, the image file is a ${typeof imageAsFile}`)
		}
		const uploadTask = ref(`/images/${imageAsFile.name}`)
		storage.put(imageAsFile)
		//initiates the firebase side uploading
		uploadTask.on(
			'state_changed',
			(snapShot) => {
				//takes a snap shot of the process as it is happening
				console.log(snapShot)
			},
			(err) => {
				//catches the errors
				console.log(err)
			},
			() => {
				// gets the functions from storage refences the image storage in firebase by the children
				// gets the download url then sets the image from firebase as the value for the imgUrl key:
				ref('images')
				storage
					.child(imageAsFile.name)
					.getDownloadURL()
					.then((fireBaseUrl) => {
						setImageAsUrl((prevObject) => ({
							...prevObject,
							imgUrl: fireBaseUrl,
						}))
					})
			}
		)
	}

	const submitHandler = async (e) => {
		e.preventDefault()
		const newPost = {
			userId: user._id,
			desc: desc.current.value,
		}
		if (file) {
			const data = new FormData()
			const fileName = Date.now() + file.name
			data.append('name', fileName)
			data.append('file', file)
			newPost.img = fileName
			console.log(newPost)
			try {
				await axios.post('/upload', data).then((res) => {
					window.location.reload()
				})
			} catch (err) {}
		}
		try {
			await axios.post('/posts', newPost).then((res) => {
				window.location.reload()
			})
		} catch (err) {}
	}
	return (
		<div className="share">
			<div className="shareWrapper">
				<div className="shareTop">
					<img
						className="shareProfileImg"
						src={
							user.profilePicture
								? user.profilePicture
								: 'https://qph.fs.quoracdn.net/main-qimg-cf89e8e6daa9dabc8174c303e4d53d3a'
						}
						alt=""
					/>
					<input
						placeholder={`What's on your mind ${user.username} ?`}
						className="shareInput"
						ref={desc}
					/>
				</div>
				<hr className="shareHr" />
				<form className="shareBottom" onSubmit={handleFireBaseUpload}>
					<div className="shareOptions">
						<label htmlFor="file" className="shareOption">
							<PermMedia htmlColor="#9381FF" className="shareIcon" />
							<span className="shareOptionText">Photo</span>
							<input
								type="file"
								style={{ display: 'none' }}
								id="file"
								accept=".png,.jpeg,.jpg"
								onChange={handleImageAsFile}
							/>
						</label>
						<div className="shareOption">
							<Label htmlColor="#9381FF" className="shareIcon" />
							<span className="shareOptionText">Tag</span>
						</div>
						<div className="shareOption">
							<Room htmlColor="#9381FF" className="shareIcon" />
							<span className="shareOptionText">Location</span>
						</div>
						<div className="shareOption">
							<EmojiEmotions htmlColor="#9381FF" className="shareIcon" />
							<span className="shareOptionText">Feelings</span>
						</div>
						<div className="imageOption">
							{isImage && (
								<span className="shareOptionText">Image Uploaded</span>
							)}
						</div>
					</div>
					<button type="submit" className="shareButton">
						Share
					</button>
				</form>
			</div>
		</div>
	)
}
