import { createContext, useReducer } from 'react'
import AuthReducer from './AuthReducer'

const INITIAL_STATE = {
	_id: '61e06332710a26db1a1933b9',
	username: 'sav',
	email: 'sav@email.com',
	password: '$2b$10$Hk8gqisxCrJM.qJxnKpJc.USReC/Q4yXNyR8QfeH/IV.RNQx0vLGm',
	profilePicture: '',
	coverPicture: '',
	followers: [],
	followings: [],
	isAdmin: false,
	desc: 'I love my boyfriend he is the best',
	city: 'Louisville',
	from: 'None your busioness',
	relationship: 2,
}

export const AuthContext = createContext(INITIAL_STATE)

export const AuthContextProvider = ({ children }) => {
	const [state, dispatch] = useReducer(AuthReducer, INITIAL_STATE)

	return (
		<AuthContext.Provider
			value={{
				user: state.user,
				isFetching: state.isFetching,
				error: state.error,
				dispatch,
			}}
		>
			{children}
		</AuthContext.Provider>
	)
}
